INSERT INTO `posts` (`id`, `user_id`, `title`, `type`, `first_content`, `wallpaper`, `views`, `created_at`, `updated_at`, `second_content`, `third_content`, `info`, `system_l`, `system_b`, `likes`, `dislikes`, `downloads`, `files_setup`, `files_info`, `download`, `tags`) VALUES
(25, 1, 'دانلود مراسم E3 2021 – نمایشگاه سرگرمی های الکترونیکی ۲۰۲۱', 'مالتی مدیا', '<p dir=\"rtl\">مراسم E۳ ۲۰۲۱ به&zwnj;عنوان بیست و ششمین دوره از کنفرانس&zwnj;های E۳ محسوب می&zwnj;شود که برای دومین سال پیاپی، در دوران کرونا و با تاثیرپذیری آن برگزار می&zwnj;شود. کمپانی سونی بازهم در این مراسم حضور ندارد و همین موضوع سبب شده است که برگزار کنندگان با چالش&zwnj;های بسیار زیادی روبرو شوند. کنفرانس&zwnj;های مختلف این رویداد، همواره پیش از آغاز رسمی E۳ برگزار شده و این بار نیز همین روند برقرار بوده است. از مهم&zwnj;ترین تفاوت&zwnj;های امسال می&zwnj;توانیم به برگزاری مشترک کنفرانس ایکس باکس و بتسدا اشاره کنیم که بعد از خریداری شدن بتسدا، تصمیم گرفته&zwnj;اند که کنفرانس خود را با همدیگر برگزار کنند.</p>\r\n\r\n<p dir=\"rtl\">&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;</p>\r\n\r\n<p dir=\"rtl\"><span style=\"color:#ff۰۰۰۰\"><strong>آپدیت (۲۶م خرداد ۱۴۰۰ ساعت ۰۲:۱۰):</strong></span> کنفرانس&zwnj; Nintendo Direct اضافه شد</p>\r\n\r\n<p dir=\"rtl\"><span style=\"color:#ff۰۰۰۰\"><strong>آپدیت (۲۵م خرداد ۱۴۰۰ ساعت ۱۲:۰۰):</strong></span> کنفرانس&zwnj;های Capcom و Razer اضافه شد</p>\r\n\r\n<p dir=\"rtl\"><span style=\"color:#ff۰۰۰۰\"><strong>آپدیت (۲۵م خرداد ۱۴۰۰ ساعت ۱۱:۱۴):</strong></span> کنفرانس مایکروسافت و بتسدا با کیفیت اولترا اچ دی WEB-DL ۲۱۶۰p ۴K UHD اضافه شد</p>', 'images/posts/post-25/wallpaper/E3-2021-Cover-Large.jpg', 5, '2021-06-17 00:47:01', '2021-08-14 15:00:44', NULL, NULL, NULL, NULL, NULL, 54, 5, 0, NULL, NULL, NULL, NULL),
(26, 1, 'دانلود مستند مزرعه کلارکسون Clarkson’s Farm S01 (2021)', 'مالتی مدیا', '<p dir=\"rtl\">کلارکسون در مستند Clarkson&rsquo;s Farm ۲۰۲۱ در تلاش برای راه اندازی یک مزرعه در حومه شهر است و برای این منظور، ماجراجویی&zwnj;های متنوعی را دنبال خواهد کرد. این مجری سرشناس، از رانندگی اتومبیل&zwnj;های مدرن فرار، لامبورگینی و برندهای دیگر، هم اکنون به تراکتورسواری روی آورده است و قصد دارند که با کمک متخصصین حاضر در حومه شهر، مزرعه خود را کم کم راه اندازی کند. در این راه، چالش&zwnj;های هیجان انگیزی اتفاق خواهد افتاد و برای بینندگان مستند گرند تور هم می&zwnj;تواند بسیار جذاب باشد. شم شوخ طبعی کلارکسون در این مستند هم وجود دارد و در هر قسمت می&zwnj;توانید از تماشای آن لذت ببرید. کلارکسون با مشکلات زیادی در این راه روبرو شده و سختی&zwnj;های زیادی را متحمل می&zwnj;شود. او یک تراکتور ساخت لامبورگینی را سوار شده و به اطراف می&zwnj;رود. او حیوانات زیادی را در مزرعه خود وارد کرده و قصد دارند که آن&zwnj;ها را مدیریت کند.</p>', 'images/posts/post-26/wallpaper/Clarksons-Farm-2021-Cover-Large.jpg', 15, '2021-06-17 00:51:37', '2021-08-14 15:00:32', NULL, NULL, NULL, NULL, NULL, 0, 0, 0, NULL, NULL, NULL, NULL),
(27, 1, 'دانلود فصل اول انیمیشن یاسوکه Yasuke 2021 – با زیرنویس فارسی', 'مالتی مدیا', '<p dir=\"rtl\">نام انیمیشن: Yasuke &ndash; یاسوکه</p>\r\n\r\n<p dir=\"rtl\">سال انتشار: ۲۰۲۱</p>\r\n\r\n<p dir=\"rtl\">ژانر: انیمیشن، اکشن، ماجرایی، درام، فانتزی، هیجان انگیز</p>\r\n\r\n<p dir=\"rtl\">مدت زمان: هر قسمت حدود ۳۰ دقیقه</p>\r\n\r\n<p dir=\"rtl\"><span style=\"color:#ff۰۰۰۰\"><strong>صدای انیمیشن: دوزبانه &ndash; انگلیسی + ژاپنی</strong></span></p>\r\n\r\n<p dir=\"rtl\">وضعیت پخش: اتمام فصل اول</p>\r\n\r\n<p dir=\"rtl\">کیفیت: WEBRip ۶Channel DUAL ۱۰۸۰p / WEBRip DUAL ۷۲۰p</p>\r\n\r\n<p dir=\"rtl\">زیرنویس فارسی: دارد؛ فصل اول به صورت کامل</p>\r\n\r\n<p dir=\"rtl\">زیرنویس انگلیسی: دارد؛ فصل اول به صورت کامل (ادغام&zwnj;شده با ویدیو)</p>\r\n\r\n<p dir=\"rtl\"><a href=\"https://www.imdb.com/title/tt۹۳۱۰۳۳۰/\" rel=\"nofollow noopener\" target=\"_blank\">IMDB</a></p>\r\n\r\n<p dir=\"rtl\">خلاصه داستان: انیمیشن یاسوکه (به انگلیسی: Yasuke ۲۰۲۱) در مورد شخصیتی به همین نام است که تازه از آفریقا رسیده و قصد دارد که در کنار یک فئودال توانا در ژاپن به نبردهای وحشیانه&zwnj;ای پایان دهد. این نبردها در قرن شانزدهم جریان داشته و می&zwnj;تواند برای جهان بسیار خطرناک باشد. در هر قسمت از این انیمیشن، شاهد هنرنمایی کسی هستیم که همه او را سامورایی سیاه نامگذاری کرده&zwnj;اند. او قصد دارد که به یک افسانه تبدیل شده و مردم کشور خود را نجات دهید. او شخصیت پیچیده&zwnj;ای داشته و به همین دلیل، اطرافیان چندانی دور خود ندارد.</p>', 'images/posts/post-27/wallpaper/Yasuke-2021-Cover-Large.jpg', 13, '2021-06-17 00:53:41', '2021-08-14 15:01:02', NULL, NULL, NULL, NULL, NULL, 1, 0, 0, NULL, NULL, NULL, NULL),
(28, 1, 'دانلود مجموعه کامل انیمیشن Teen Titans GO S01-S07 (2021)', 'مالتی مدیا', '<p dir=\"rtl\">نام انیمیشن: Teen Titans GO Season ۱-۷ &ndash; فصل اول الی هفتم انیمیشن سریالی تایتان های جوان حرکت!</p>\r\n\r\n<p dir=\"rtl\">سال انتشار: ۲۰۱۳-۲۰۲۱</p>\r\n\r\n<p dir=\"rtl\">مدت زمان: به طور میانگین هر قسمت ۱۱ دقیقه</p>\r\n\r\n<p dir=\"rtl\">حجم: به طور میانگین هر قسمت ۸۲/۶۴ مگابایت</p>\r\n\r\n<p dir=\"rtl\">کیفیت: WEB-DL ۷۲۰p / WEB-DL x۲۶۵ ۷۲۰p</p>\r\n\r\n<p dir=\"rtl\"><a href=\"http://www.imdb.com/title/tt۲۷۷۱۷۸۰/\" rel=\"noopener noreferrer\" target=\"_blank\">IMDB</a></p>\r\n\r\n<p dir=\"rtl\">زیرنویس فارسی: <strong>دارد، اختصاصی دانلودها</strong></p>\r\n\r\n<p dir=\"rtl\">خلاصه داستان: انیمیشن سریالی Teen Titans GO! که سری جدید از انیمیشن تایتان های جوان می باشد، تا به حال در طی چهار فصل از سال ۲۰۱۳ الی ۲۰۱۷ عرضه شده است. وبسایت دانلودها تصمیم گرفته است تا تمامی قسمت های این انیمیشن را به صورت کامل برای شما عزیزان آماده کند. انیمیشن سریالی تایتان های جوان حرکت! (به انگلیسی Teen Titans GO!) ساخته کشور امریکا می باشد که بر اساس کمیک های تیم هیروهای خیالی کمپانی DC یعنی همان تایتان های جوان پیاده شده است.</p>\r\n\r\n<p dir=\"rtl\">سری Teen Titans GO هیچ ارتباطی با بخش پیشین خود نداشته و داستانی کاملا جدید و مجزا را روایت می کند. انیمیشن سریالی تایتان های جوان حرکت! روایتگر ماجراجویی های تایتان های جوان ساکن در شهر Jump City می باشد در حالی که آن ها هنوز نوجوان بوده و درگیر نبردها و آشوب های بزرگسالان نشده اند و &hellip;</p>', 'images/posts/post-28/wallpaper/Teen-Titans-GO-tv-series-cover-large.jpg', 1, '2021-06-17 00:55:16', '2021-08-14 11:10:44', NULL, NULL, NULL, NULL, NULL, 0, 1, 0, NULL, NULL, NULL, NULL),
(29, 1, 'دانلود فصل دوم انیمیشن خانه جغد The Owl House S02 2021', 'مالتی مدیا', '<p dir=\"rtl\">نام انیمیشن: The Owl House &ndash; خانه جغد</p>\r\n\r\n<p dir=\"rtl\">سال انتشار: ۲۰۲۰-۲۰۲۱</p>\r\n\r\n<p dir=\"rtl\">ژانر: انیمیشن، اکشن، ماجرایی</p>\r\n\r\n<p dir=\"rtl\">مدت زمان: به طور میانگین هر قسمت ۲۲دقیقه</p>\r\n\r\n<p dir=\"rtl\">کیفیت: WEB-DL x۲۶۵ ۱۰۸۰p / WEB-DL x۲۶۵ ۷۲۰p / WEB-DL ۷۲۰p</p>\r\n\r\n<p dir=\"rtl\"><span style=\"color:#ff۰۰۰۰\">وضعیت پخش: در حال پخش فصل دوم&hellip;</span></p>\r\n\r\n<p dir=\"rtl\">زیرنویس فارسی: دارد، اختصاصی وبسایت دانلودها</p>\r\n\r\n<p dir=\"rtl\">زیرنویس انگلیسی: دارد، ادغام شده با ویدیو</p>\r\n\r\n<p dir=\"rtl\"><a href=\"https://www.imdb.com/title/tt۸۰۵۰۷۵۶/\" rel=\"nofollow noopener noreferrer\" target=\"_blank\">IMDB</a></p>\r\n\r\n<p dir=\"rtl\">خلاصه داستان: انیمیشن خانه جغد (The Owl House) در مورد نوجوانی است که در مکانی به نام Demon Realm گرفتار می شود و قصد دارد که با نیروهای شر مقابله کند. بدین ترتیب یک جادوگر و یک جنگجوی ریزه میزه هم در کنار وی حضور دارند و این سه نفر قصد دارند که تمام نیروهای شیطانی که سر راه خود می بینند را نابود سازند. این ماموریت در هر قسمت لحظات هیجان انگیزی را ایجاد می کند و نوجوان ما می فهمد که نقطه ضعف نیروهای شیطانی کجاها هستند.</p>\r\n\r\n<p dir=\"rtl\">شخصیت دوست داشتنی ماجرا که لوز نام دارد توسط یک پورتال وارد این جهان مخوف می شود و موجوداتی را می بیند که در عین ترسناک بودند گویا دوست و رفیق وی هستند. فضای فان و شاد در انیمیشن برقرار است و مدام می توانید از گفتگوی میان شخصیت ها لذت ببرید. لوز مناطق مختلف این جهان را اکتشاف می کند و متوجه می شود که واقعا چه نیروهایی قصد نابودی دنیای زیبایشان را دارند. دوست و دشمن مرز باریکی را باهم تشکیل می دهند و لوز باید بفهمد که چه شیاطینی در لباس دوست به وی نزدیک می شوند.</p>', 'images/posts/post-29/wallpaper/The-Owl-House-2020-Cover-Large.jpg', 0, '2021-06-17 00:56:33', '2021-07-13 13:08:21', NULL, NULL, NULL, NULL, NULL, 2, 0, 0, NULL, NULL, NULL, NULL),
(30, 1, 'دانلود فصل دوم انیمیشن آمفیبیا – Amphibia Season 2 (2019-2021)', 'مالتی مدیا', '<p dir=\"rtl\">نام انیمیشن: Amphibia &ndash; فصل اول و دوم انیمیشن آمفیبیا</p>\r\n\r\n<p dir=\"rtl\">سال انتشار: ۲۰۱۹-۲۰۲۱</p>\r\n\r\n<p dir=\"rtl\">ژانر: انیمیشن، ماجرایی، کمدی</p>\r\n\r\n<p dir=\"rtl\">مدت زمان: هر قسمت حدود ۲۲ دقیقه</p>\r\n\r\n<p dir=\"rtl\">کیفیت: WEBRip ۷۲۰p / WEBRip x۲۶۵ ۷۲۰p / WEBRip x۲۶۵ ۱۰۸۰p</p>\r\n\r\n<p dir=\"rtl\"><span style=\"color:#ff۰۰۰۰\">وضعیت پخش: اتمام فصل دوم</span></p>\r\n\r\n<p dir=\"rtl\">زیرنویس فارسی: دارد، اختصاصی وبسایت دانلوها!</p>\r\n\r\n<p dir=\"rtl\">زیرنویس انگلیسی: دارد، ادغام شده با ویدیو</p>\r\n\r\n<p dir=\"rtl\"><a href=\"https://www.imdb.com/title/tt۸۰۵۰۷۴۰/\">IMDB</a></p>\r\n\r\n<p dir=\"rtl\">خلاصه داستان: انیمیشن سریالی آمفیبیا (به انگلیسی: Amphibia) در مورد دختری معمولی به نام آنه بونشوی ۱۳ ساله است که گنجینه ای هیجان انگیز پیدا و توسط آن به دنیای آمفیبیا سفر می کند. آمفیبیا جهانی پر از قورباغه ها و حشرات غول پیکر است و او آمده است که برای اولین بار یک رابطه دوستی در اینجا برای خودش پیدا کند. او در دنیای واقعی ناموفق بوده است و حال سعی می کند که از اسپرینگ کمک بگیرد و دوستان جدید زیادی برای خودش پیدا کند. دنیای جدید کاملا جادیی است و به نوعی همه آنها تبدیل به قوباغه ها و حشرات مختلفی شده اند. فضای شاد و سرگرم کننده ای در این انیمیشن وجود دارد که آنه آن را بسیار دوست دارد و فرصتی است که به ماجراجویی های موردعلاقه اش بپردازد.</p>', 'images/posts/post-30/wallpaper/Amphibia-2019-Cover-Large.jpg', 0, '2021-06-17 01:31:41', '2021-06-26 14:27:12', NULL, NULL, NULL, NULL, NULL, 0, 0, 0, NULL, NULL, NULL, NULL),
(98, 1, 'دانلود ویندوز ۱۱ – Windows 11 Build 21996.1 Consumer Edition', 'نرم افزار', '<p dir=\"rtl\" style=\"text-align:center\"><span style=\"color:#e74c3c\"><strong>|به همراه آموزش نصب تصویری کامل ویندوز ۱۱ &ndash; انحصاری دانلودها|</strong></span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:center\"><span style=\"color:#2ecc71\"><strong>|آموزش رفع مشکل This PC Can&rsquo;t Run Windows ۱۱ یا همان TPM ۲.۰ اضافه شد|</strong></span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">بالاخره و بعد از مدت&zwnj;ها نسخه جدیدی از سیستم عامل ویندوز ارائه شده و بخش اعظمی از تغییرات آن، مربوط به رابط کاربری خواهد بود. طی روزهای آینده، مایکروسافت قرار است که از جدیدترین نسخه ویندوز خود با اسم رمز Sun Valley رونمایی کند. بخش قابل توجهی از تغییرات UX این ویندوز، از پوسته ویندوز ۱۰X الهام گرفته است و درواقع، نسخه ۱۰X راهی بازار نخواهد شد. همان&zwnj;طور که انتظار می&zwnj;رفت، طی هفته&zwnj;های گذشته، اطلاعات جدیدی از ویندوز ۱۱ فاش شده و طرفداران توانستند نگاهی به رابط کاربری آن بیندازند. Windows ۱۱ از طراحی کاملا جدیدی برخوردار خواهد بود. مایکروسافت طراحی جدید و جذابی برای آن تدارک دیده است و مدت&zwnj;ها بود که با اسم رمز Sun Valley در حال طراحی و اماده سازی آن بود.</p>', 'images/posts/post-98/wallpaper/Windows-11-Cover.jpg', 11, '2021-06-21 12:22:35', '2021-08-14 14:53:37', '<p dir=\"rtl\">زمینه شفاف ویندوز ۱۱ با تاری قابل تغییر، در همه بخش&zwnj;های ویندوز وجود خواهد داشت و می&zwnj;توانیم شاهد سبک جزیره&zwnj;ای نمایش پنجره&zwnj;ها در هنگام جستجوی وب باشیم. ویندوز ۱۱ دارای آیکون&zwnj;های جدیدی خواهد بود و دیگر شاهد آیکون&zwnj;های ویندوز ۹۵ نخواهیم بود. مایکروسافت سرانجام تصمیم گرفته است که آیکون&zwnj;هایی که در سیستم مورد استفاده قرار می&zwnj;گیرد را به کل تغییر داده و دیگر از سبک&zwnj;های قدیمی ویندوز ۹۵ استفاده نکند. ویندوز ۱۱ دارای نوآوری&zwnj;های کاربردی خواهد بود، اما این تغییرات چندان زیاد نخواهد بود. به حال در آینده اطلاعات بیشتری می&zwnj;توانیم کسب کنیم.</p>', NULL, '<p>Windows 11 will receive a completely new design. Microsoft clearly needs a good reason to reverse its past claims and still ditch Windows 10 by introducing a new operating system number. And a completely new design is great for that. The Redmond giant has long been preparing a redesign for an update with the code name Sun Valley (&ldquo;Sun Valley&rdquo;) &ndash; apparently, under this naming it was Windows 11. The Sun Valley project flashed on the network for a long time &ndash; Microsoft regularly disclosed details of the new interface style, insiders shared the unknown previously information, and popular designers in their circles drew realistic concepts based on all this data.</p>', '<p>Processor: Dual-core 1.2GHz (32-bit) or 1.4GHz (64-bit)</p>\r\n\r\n<p>RAM: 2GB</p>\r\n\r\n<p>Display: 1024&times;768 pixels</p>\r\n\r\n<p>Hard Disk Capacity: 15GB (at least)</p>', '<p style=\"text-align:justify\">Processor: Quad-core 2GHz or faster</p>\r\n\r\n<p style=\"text-align:justify\">RAM: 4GB or more</p>\r\n\r\n<p style=\"text-align:justify\">Display: 1680&times;1050 pixels</p>\r\n\r\n<p style=\"text-align:justify\">Hard Disk Capacity: 18GB or more (for installation only)</p>', 10, 2, 0, '<div class=\"panel-body\" dir=\"rtl\">\r\n<p>&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&ndash;</p>\r\n\r\n<p><a href=\"https://soft1.downloadha.com/NarmAfzaar/June2021/Windows.11.Installation.Guide_www.Downloadha.com_.rar\"><span style=\"color:#ff0000\"><strong>دانلود و مشاهده آموزش نصب تصویری کامل ویندوز 11 با فعال&zwnj;سازی کامل</strong></span></a></p>\r\n\r\n<p>&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&ndash;</p>\r\n\r\n<p><span style=\"color:#339966\"><strong>آموزش نصب نوشتاری:</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">1) فایل&zwnj;های فشرده را دانلود کرده و توسط نرم افزار WinRar اکسترکت کنید تا فایل ISO به شما داده شود.</p>\r\n\r\n<p style=\"text-align:justify\">2) فایل ISO بدست آمده را از طریق نرم افزار RUFUS به صورت بوتیبل بر روی فلش خود کپی کنید و یا از طریق نرم افزارهای رایت ایمیج، آن را بر روی DVD رایت نمایید.</p>\r\n\r\n<p style=\"text-align:justify\">3) ویندوز را نصب کنید. در ابتدای نصب از شما درخواست سریال نامبر برای نصب می&zwnj;شود، لطفا گزینه I Don&rsquo;t Have a License Key را بزنید و نصب را ادامه بدهید.</p>\r\n\r\n<p style=\"text-align:justify\">4) پس از اتمام نصب، لطفا فایل فعال ساز را اجرا کنید. بر روی آیکون ویندوز کلیک کنید (نزدیک به گزینه Settings می&zwnj;باشد). وارد زبانه Activation شده و گزینه EZ-Activator را انتخاب نمایید.</p>\r\n\r\n<p style=\"text-align:justify\">5) اجازه بدهید تا عملیات فعال سازی انجام شود و ویندوز شما اکتیو شود.</p>\r\n\r\n<p>&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&ndash;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#339966\"><strong>آموزش رفع مشکل This PC Can&rsquo;t Run Windows 11 یا همان TPM 2.0</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">کاربران عزیز لطفا دقت داشته باشید که این مشکل اکثرا در سیستم&zwnj;هایی بوجود می&zwnj;آید که از قطعات AMD استفاده می&zwnj;کنند. برای رفع این مشکل، 2راه پیش روی شماست:</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff6600\"><strong>روش اول)</strong> </span>اگر مادربرد شما از قابلیت Secure Boot پشتیبانی کند، لطفا گزینه Secure Boot را در تنظیمات ابتدایی سیستم خود (BIOS) اعمال کنید. البته گزینه CPU TPM هم در صورت وجود در قابلیت&zwnj;های سیستم شما بایستی فعال شود.</p>\r\n\r\n<p style=\"text-align:justify\">&mdash;&mdash;&mdash;-</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff6600\"><strong>روش دوم)</strong> </span>اگر قابلیت&zwnj;های مورد اول را ندارید، لطفا فایل زیر را دانلود کنید:</p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"https://dl6.downloadha.com/hosein/soft/June2021/TPM.2.0.FiX.Win11_www.Downloadha.com_.zip\">دانلود فایل فیکس برای مشکل TPM 2.0 از لینک مستقیم</a></p>\r\n\r\n<p style=\"text-align:justify\">فایل ISO ویندوز خود را توسط RUFUS بر روی USB Flash خود نصب کنید. حالا داخل فایل فلش خود شده و به پوشه sources مراجعه نمایید. فایل فیکس که دانلود کرده اید (فایل با پسوند .dll) را در این پوشه کپی کنید و اگر پیغام replace داده شد، تایید کنید.</p>\r\n\r\n<p style=\"text-align:justify\">حال از طریق بوت، اقدام به نصب نمایید و بایستی مشکل TPM 2.0 برای شما برطرف شده باشد.</p>\r\n\r\n<p style=\"text-align:justify\">&mdash;&mdash;&mdash;-</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff6600\"><strong>روش سوم)</strong> </span>در صفحه&zwnj;ای که خطای TPM 2.0 به شما داده می&zwnj;شود، لطفا کلید ترکیبی Shift+F10 را بزنید تا صفحه CMD برای شما نمایش داده شود. سپس دستور زیر را اجرا کنید و مجدد برای نصب تلاش نمایید:</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#ff0000\">gpupdate /force</span></p>\r\n</div>', '<ul>\r\n	<li>حجم فایل: 4.1گیگابایت</li>\r\n	<li>شرکت سازنده:</li>\r\n	<li>تاریخ انتشار: JUN-18-2021</li>\r\n	<li>منبع: <a href=\"https://www.downloadha.com\"> دانلود ها - دانلود رایگان نرم افزار،بازی،فیلم و سریال</a></li>\r\n	<li>قیمت: نامشخص</li>\r\n</ul>', '<div dir=\"rtl\" id=\"dlhaid2\">\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>هشدار بسیار مهم!</strong></span> نسخه فعلی، نسخه غیررسمی و لیک&zwnj;شده از ویندوز 11 می&zwnj;باشد. گویا قرار است مایکروسافت در 24م ژوئن 2021 ویندوز 11 را به صورت رسمی در دسترس کاربران قرار بدهد.</p>\r\n\r\n<p>----------------------</p>\r\n\r\n<p><a href=\"https://dl6.downloadha.com/hosein/soft/June2021/Windows.11.DEV.build.21996.1_ACTIVATOR_www.Downloadha.com_.rar\">دانلود فعال&zwnj;ساز مناسب برای ویندوز 11 - نسخه Consumer Dev Build Activator</a></p>\r\n\r\n<p>----------------------</p>\r\n\r\n<p><strong>دانلود ویندوز 11 - باندل کانسیومر ریلیز غیررسمی (لیک شده):</strong></p>\r\n\r\n<p><span style=\"color:#008000\">توضیحات بیشتر:</span> Build 21996.1.210529 - Consumer Edition DEV</p>\r\n\r\n<p><span style=\"color:#008000\">نسخه&zwnj;های موجود در این باندل:</span> Home, Education, Pro, Pro Education, Pro for Workstation</p>\r\n\r\n<p><a href=\"https://dl6.downloadha.com/hosein/soft/June2021/Windows.11.DEV.build.21996.1.Consumer.x64_www.Downloadha.com_.part1.rar\">دانلود پارت 1 با لینک مستقیم</a></p>\r\n\r\n<p><a href=\"https://dl6